mapper.update:
update t_b_apply
set totalfee = (select SUM(fee) from t_b_prescription where applyid = #{id})
where id = #{id}

mapper.select:
SELECT t1.* FROM t_b_apply t1

    and t1.dept = #{dept}

        and (patientname like CONCAT('%',#{name},'%') or pinyin like CONCAT('%',#{name},'%'))


        and t1.status = #{status}


        and DATE(t1.appointmenttime) =  DATE(#{date})


        and t1.ispaid = #{isPaid}
