mapper.update:
update t_b_apply
set totalfee = (select SUM(fee) from t_b_prescription where applyid = #{id})
where id = #{id}

mapper.select:
SELECT t1.id, -- 挂号单id t3.avatar, -- 头像 t2.idcard, -- 身份证号 t2.mobile, -- 手机号 t1.clinicno, -- 门诊编号 t2.age, t1.orgcode, t1.orgname, t1.patientid, t2.id as patientItemId, t1.patientname, t1.gender, t1.pinyin, t1.fee, t1.ispaid, t1.feetype, t1.expire, t1.`status`, t1.dept, t1.deptname, t1.totalfee, t2.socialcard, t2.address, t1.appointmenttime, t1.isfirst, t2.anaphylaxis, t2.personalhistory, t2.familyhistory FROM t_b_apply t1
LEFT JOIN t_b_patient_item AS t2 ON t2.id = t1.patientitemid LEFT JOIN t_b_patient AS t3 ON t3.id = t1.patientid
AND t1.appointmenttime >= #{startTime}
AND t1.appointmenttime <= #{endTime}
and t1.dept = #{dept}
and t1.orgcode = #{orgCode}
and (t1.patientname like CONCAT('%',#{param},'%') or t1.pinyin like CONCAT('%',#{param},'%') OR t1.clinicno like CONCAT('%',#{param},'%'))
and t1.status = #{status}
and t1.ispaid = #{isPaid}
ORDER BY t1.clinicno ASC

select count(1) from t_b_apply t1


        and t1.doctorid = #{userId}


         AND t1.appointmenttime >= #{startTime} 


          AND t1.appointmenttime <= #{endTime}

SELECT t1.*,t2.avatar FROM t_b_apply t1
LEFT JOIN t_p_s_user t2 ON t1.doctorid = t2.id


        and t1.patientid = #{patientId}


        and t1.createby = #{createBy}
