mapper.select:
select t1.id ,t1.tplname ,t1.category,t1.isvalid , t1.ispublic,t1.diagnosis,t1.createby, t2.dicitemname as categoryName,t3.username as createUserName, if(t1.createby=#{userInfo.id} ,1,0) as isSelf from t_b_diagnosis_tpl t1 JOIN t_p_s_dicitem t2 on t1.category = t2.dicitemcode LEFT JOIN t_p_s_user t3 on t1.createby = t3.id where t1.orgcode = #{userInfo.orgCode} and t2.dictypecode = #{dicTypeCode} and t1.removed = '0'
and t1.tplname like concat('%',#{name},'%')
and t1.createby = #{userInfo.id} and t1.isPublic = #{isPublic}
and t1.ispublic = #{isPublic}
and (t1.ispublic = 1 or t1.createby = #{userInfo.id})
and t1.isvalid = #{isValid}
order by t1.createat desc

select count(1)
from t_b_diagnosis_tpl t1
JOIN t_p_s_dicitem t2 on t1.category  = t2.dicitemcode
where t1.orgcode = #{userInfo.orgCode} and t2.dictypecode = #{dicTypeCode} and t1.removed = '0'

    and t1.tplname like concat('%',#{name},'%')


    and t1.createby = #{userInfo.id} and t1.isPublic = #{isPublic}


    and t1.ispublic = #{isPublic}


    and (t1.ispublic = 1 or  t1.createby = #{userInfo.id})


    and t1.isvalid = #{isValid}
