mapper.select:
select * from t_b_drugdict dict where NOT EXISTS (select * from t_b_drug where dictid = dict.id and removed= '0' and orgCode = #{orgCode} )
and (name like concat('%',#{name},'%') or pinyin like concat('%',#{name},'%') or goodsname like concat('%',#{name},'%') or goodspinyin like concat('%',#{name},'%') or barcode like concat('%',#{name},'%'))
and category = #{category}

select count(1) from t_b_drugdict dict where NOT EXISTS (select * from t_b_drug where dictid = dict.id and removed= '0' and orgCode = #{orgCode} )
and (name like concat('%',#{name},'%') or pinyin like concat('%',#{name},'%') or goodsname like concat('%',#{name},'%') or goodspinyin like concat('%',#{name},'%') or barcode like concat('%',#{name},'%'))
and category = #{category}
