mapper.select:
select * from t_b_drugdict dict where dict.removed = '0' AND dict.id NOT IN (select dictid from t_b_drug where removed= '0' and orgCode = #{orgCode} AND dictid IS NOT NULL )
and (name like concat('%',#{name},'%') or pinyin like concat('%',#{name},'%') or goodsname like concat('%',#{name},'%') or goodspinyin like concat('%',#{name},'%') or barcode like concat('%',#{name},'%'))
and category = #{category}

select count(1) from t_b_drugdict dict where dict.removed = '0' AND NOT EXISTS (select * from t_b_drug where dictid = dict.id and removed= '0' and orgCode = #{orgCode} )
and (name like concat('%',#{name},'%') or pinyin like concat('%',#{name},'%') or goodsname like concat('%',#{name},'%') or goodspinyin like concat('%',#{name},'%') or barcode like concat('%',#{name},'%'))
and category = #{category}

select * from t_b_drugdict  where removed = '0'

    and (name like concat('%',#{name},'%') or pinyin like concat('%',#{name},'%')
    or goodsname like concat('%',#{name},'%') or goodspinyin like concat('%',#{name},'%')
    or barcode like concat('%',#{name},'%'))


    and category = #{category}


    and ishandle = #{isHandle}
