mapper.select:
select t1.* from t_b_drug t1 LEFT JOIN t_b_manufacturer t2 on t1.manufacturer = t2.id where t1.orgCode = #{orgCode} AND t1.removed = '0'
and (t1.name like concat('%',#{name},'%') or t1.pinyin like concat('%',#{name},'%') or t1.goodsname like concat('%',#{name},'%') or t1.goodspinyin like concat('%',#{name},'%') )
and t1.category = #{category}
and t1.isValid = #{isValid}

select count(1) from t_b_drug t1 where orgCode = #{orgCode} and removed = '0'

    and (t1.name like concat('%',#{name},'%') or t1.pinyin like concat('%',#{name},'%') or
    t1.goodsname like concat('%',#{name},'%') or t1.goodspinyin like concat('%',#{name},'%') )


    and t1.category = #{category}


    and t1.isValid = #{isValid}
