mapper.select:
select * from t_p_s_org where removed = '0'
AND lng BETWEEN #{lngBegin} AND #{lngEnd}
AND lat BETWEEN #{latBegin} AND #{latEnd}

    and orgname like CONCAT('%',#{orgName},'%')

SELECT t1.*, t2.dicitemname categorystr , t3.dicitemname levelstr, t4.loginname, t5.name provinceStr, t6.name cityStr, t7.name countryStr, t8.gradeoneprice, t8.gradetwoprice, t8.gradethreeprice, t8.accompanyingprice FROM t_p_s_org t1 LEFT JOIN ( SELECT * FROM t_p_s_dicitem WHERE dictypecode = 'OrgCategory' )as t2 ON t2.dicitemcode = t1.category LEFT JOIN ( SELECT * FROM t_p_s_dicitem WHERE dictypecode = 'OrgLevel' )as t3 ON t3.dicitemcode = t1.`level` LEFT JOIN t_p_s_user t4 ON t4.id = t1.manager LEFT JOIN t_p_s_area t5 ON t5.id = t1.province LEFT JOIN t_p_s_area t6 ON t6.id = t1.city LEFT JOIN t_p_s_area t7 ON t7.id = t1.country LEFT JOIN accompanying_price t8 ON t8.orgcode = t1.orgcode
AND t1.removed = '0'
and t1.orgname like CONCAT('%',#{orgName},'%')
and t1.city = #{cityId}
and t1.isrecommend = #{isRecommend}
