mapper.select:
select * from t_b_schedule where userid = #{userid}
order BY startTime DESC
limit 1

select tt.id as userid,tt.username as userName,tt.avatar,
    tr.monday,tr.tuesday,tr.wednesday,tr.thursday,tr.friday,tr.saturday,tr.sunday,tr.circle,tr.startTime,tr.endTime
from t_p_s_user tt LEFT JOIN(
      select t2.* from t_p_s_user t1 JOIN t_b_schedule t2 on t1.id = t2.userid
      where

              ((t2.starttime = #{startTime} and t2.endtime = #{endTime}) or
                  (t2.starttime <=  #{startTime}  and t2.endtime is null))


          AND t1.orgcode = #{orgCode}


          AND t1.dept = #{deptId}

  )tr on tt.id = tr.userid
  where


          AND tt.id in

              #{id}




          AND tt.orgcode = #{orgCode}


          AND tt.dept = #{deptId}

select t1.id as userid, t1.username , t1.applyfee , t1.diagnoslevel, t1.duty, t1.expertin , t1.introduction, t1.avatar, t2.monday,t2.tuesday,t2.wednesday,t2.thursday,t2.friday,t2.saturday,t2.sunday,t2.circle,t2.startTime,t2.endTime from t_p_s_user t1 JOIN t_b_schedule t2 on t1.id = t2.userid where
((t2.starttime = #{startTime} and t2.endtime = #{endTime}) or (t2.starttime <= #{startTime} and t2.endtime is null))
AND tt.orgcode = #{orgCode}
AND tt.dept = #{deptId}
