mapper.select:
select * from t_zy_order where paystatus = 0 and orgcode = #{orgCode}
and zystoreid in

    #{zyStoreId}

select zydrugid,count(1) as num from t_b_prescription_item t1 LEFT JOIN t_b_prescription t2 on t1.prescriptionid = t2.id where zydrugid in
#{drugId}
and t2.orgcode = #{orgCode} and t1.paystatus = 1 group by zydrugid

select t1.submittime,t2.username,sum(t1.totalfee) as totalfee,t1.groupnum, t1.provinceid, t1.provincename,t1.cityid,t1.cityname,t1.countyid,t1.countyname, t1.address,t1.recipient,t1.zipcode,t1.phone from t_zy_order t1 LEFT JOIN t_p_s_user t2 on t1.modifyby = t2.id where (t1.paystatus = 1 or t1.paystatus = 3) and t1.orgcode = #{orgCode} and t1.removed = '0'
and t1.submittime >= #{mo.startTime}
and t1.submittime <= #{mo.endTime}
and t1.id in (select id from t_zy_order where orderno like concat('%', #{mo.name} , '%') UNION ALL select td.id from t_zy_order_item tm LEFT JOIN t_zy_order td ON tm.orderid = td.id where tm.drugName like concat('%', #{mo.name} , '%') and td.paystatus = 1)
group by groupnum order by t1.submittime desc

select t1.id as itemId, t1.orderid, t1.applyid, t1.drugname, t1.num, t1.retailprice, t1.fee, t1.manufacturername,t1.status, t2.orderno, t2.drugfee ,t2.zyStoreId as storeId,t2.expressid,t2.expressname,t2.expressfee,t2.fullreducefee, t3.prescriptionno as prescriptionNo from t_zy_order_item t1 LEFT JOIN t_zy_order t2 on t1.orderid = t2.id LEFT JOIN t_b_prescription t3 on t1.applyid = t3.applyid where t1.status != 4 and t2.groupnum = #{groupNum}
and t2.id in (select id from t_zy_order where orderno like concat('%', #{name} , '%') UNION ALL select td.id from t_zy_order_item tm LEFT JOIN t_zy_order td ON tm.orderid = td.id where tm.drugName like concat('%', #{name} , '%') and td.paystatus = 0)
ORDER BY t2.orderno desc

select t1.id as itemId, t1.orderid, t1.applyid, t1.drugname, t1.num, t1.retailprice, t1.fee, t1.manufacturername, t2.orderno, t2.drugfee , t2.expressfee as expressFee, t2.totalfee as totalFee,t2.actualfee, t2.submittime as submitTime,t2.zyStoreId as storeId,t2.paystatus, t3.prescriptionno as prescriptionNo from t_zy_order_item t1 LEFT JOIN t_zy_order t2 on t1.orderid = t2.id LEFT JOIN t_b_prescription t3 on t1.applyid = t3.applyid where t1.removed = '0' and t1.removed = '0' and t2.orgcode = #{orgCode} and t2.paystatus in (2,4,5,6,7)
and t2.id in (select id from t_zy_order where orderno like concat('%', #{mo.name} , '%') UNION ALL select td.id from t_zy_order_item tm LEFT JOIN t_zy_order td ON tm.orderid = td.id where tm.drugName like concat('%', #{mo.name} , '%') and td.paystatus = 0)
and t2.submittime >= #{mo.startTime}
and t2.submittime <= #{mo.endTime}
ORDER BY t2.orderno desc

SELECT * FROM (
select t2.gender,t2.patientname,t2.age,t2.mobile,t1.applyid,t1.createat as submittime,0 as status from t_b_prescription t1 JOIN t_b_patient_item t2 on t1.patientid = t2.id where t1.iszydispensing = 0 and t1.orgcode = #{orgCode} and t1.id in (select prescriptionid from t_b_prescription_item where zyorderstatus is not null and zyorderstatus in (0,1,2))
UNION ALL
select t2.gender,t2.patientname,t2.age,t2.mobile,t1.applyid,t1.createat as submittime,1 as status from t_b_prescription t1 JOIN t_b_patient_item t2 on t1.patientid = t2.id where t1.iszydispensing = 0 and t1.orgcode = #{orgCode} and t1.id in (select prescriptionid from t_b_prescription_item where zyorderstatus = 4 and prescriptionid not in (select prescriptionid from t_b_prescription_item where zyorderstatus is not null and zyorderstatus in (0,1,2)))
UNION ALL
select t2.gender,t2.patientname,t2.age,t2.mobile,t1.applyid,t1.createat as submittime,2 as status from t_b_prescription t1 JOIN t_b_patient_item t2 on t1.patientid = t2.id where t1.iszydispensing = 1 and t1.orgcode = #{orgCode}
)tt
and tt.submittime >= #{mo.startTime}
and tt.submittime <= #{mo.endTime}
ORDER BY FIELD(tt.`status` ,1,0,2),tt.submittime DESC

mapper.update:
update t_b_prescription_item set zyorderstatus = #{zyOrderStatus} where id in
(
select preitemid from t_zy_order_item where orderid = #{orderId}
)
